import React, { useState, useEffect } from 'react'
import axios from 'axios';

export default function SingleArticle(props){
    const [article, setArticle] = useState({});

    function getArticle(props){
        axios.get("/api/"+props.ID).then(response => {
                setArticle(response.data);        
        })
    }

    useEffect(() => {
        getArticle(props);
    }, [props])

    if (!(Object.keys(article).length === 0 && article.constructor === Object)){
        return(
            <div className="container">
                <h1>{article.title}</h1>
                <p>{article.content}</p>
            </div>
        )
    }else{
        return(
            <div className="container h-100 vh-100 d-flex  justify-content-center">
                <h3 className="">Sorry, the article you are looking for is not available</h3>
            </div>
        )
    }
}



