import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, useParams } from 'react-router-dom';

import ListArticle from './components/ListArticle';
import SingleArticle from './components/SingleArticle';
import Home from './components/Home';
import Login from "./components/Login"
import Register from './components/Register';
import { Switch } from 'react-router-dom/cjs/react-router-dom.min';

function GetSingleArticle(){
    const { articleID } = useParams()
    return <SingleArticle ID={articleID} />
}


function App() {
    const [universalToken, setUniversalToken] = useState("");
    console.log(universalToken)
    return (
        <Router>
            <Switch>
                <Route exact path="/"><Home key={universalToken} token={universalToken}/></Route>
                <Route exact path="/article"><ListArticle token={universalToken}/></Route>
                <Route exact path="/article/:articleID"><GetSingleArticle token={universalToken}/></Route>
                <Route exact path="/login"><Login tokenize={setUniversalToken}/></Route>
                <Route exact path="/register"><Register tokenize={setUniversalToken}/></Route>
            </Switch>
        </Router>
    );
}

export default App;
