import React, { useState, useEffect, useRef } from 'react';
import { FormControl, Form, Button, Alert, Collapse } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';

// https://www.youtube.com/watch?v=EmAc4wQikwY
export default function Register(){

    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [token, setToken] = useState("");
    const [errorMsg, setErrorMsg] = useState("");
    const [isError, setIsError] = useState(false);

    function renderError(){
        if (isError){
            return(
                <Collapse in={isError} appear={isError}>
                    <Form.Text className="text-danger">
                        We'll never share your email with anyone else.
                    </Form.Text>
                </Collapse>
            )
        }
    }

    function onSubmit(e){
        e.preventDefault();
        setErrorMsg("");
        setIsError(false);
        var registerData = {
            "username": username,
            "email": email,
            "password": password,
        }
        const config = {
            headers: {
                "Content-Type": "application/json",
            }
        }

        axios.post("/account/api/auth/register", registerData)
        .then(res=>{
            if(res.data.token){
                console.log(res.data.token)
                setToken(res.data.token)
                config.headers["Authorization"] = `Token ${token}`
            } 
            if(res.data.username || res.data.email){
                setIsError(true);
                setErrorMsg(res.data.username[0])
            }
            if(res.data.email){
                setIsError(true);
                setErrorMsg(res.data.email[0])
            }
            console.log(errorMsg)
        })
        .catch(err=>{
            console.log(err)
            setIsError(true)
        })

    }
    return(
        <div className="container">
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" placeholder="Username" value={username}
                    onChange={e=>{setUsername(e.target.value)}}/>
                    <Collapse in={isError} appear={isError}>
                        <Form.Text className="text-danger">
                            We'll never share your email with anyone else.
                        </Form.Text>
                    </Collapse>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="Email" placeholder="Email" value={email}
                    onChange={e=>{setEmail(e.target.value)}}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={password}
                    onChange={e=>{setPassword(e.target.value)}}/>
                </Form.Group>

                <Button className="btn btn-primary me-3" type="submit" onClick={(e)=>{onSubmit(e)}}>
                    Register
                </Button>
                <Link to="/login" className="btn btn-dark">
                    Login
                </Link>
            </Form>
            {renderError()}

        </div>
    )
}
