import React, { useState, useEffect, useRef } from 'react';
import { FormControl, Form, Button } from 'react-bootstrap';
import axios from 'axios';

// https://www.youtube.com/watch?v=EmAc4wQikwY
export default function About(){

    const [username, setUsername] = useState("front")
    const [password, setPassword] = useState("front") 

    function onSubmit(e){
        console.log(e)
    }


    function postLogin(){
        var loginData = {
            "username": username,
            "password": password,
        }
        const config = {
            headers: {
                "Content-Type": "application/json",
            }
        }

        axios.post("/account/api/auth/signin", loginData)
        .then(res=>{
            config.headers["Authorization"] = `Token ${res.data.token}`
        })
        .then(a=>{
            axios.post("/account/api/auth/logout", null, config)
            console.log(config)

        })
        console.log(username)

    }
    var test = useRef("")
    console.log(test)
    return(
        <div className="container">
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" placeholder="Username" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <Button variant="primary" type="submit" onClick={(e)=>{console.log(e)}}>
                    Log In
                </Button>
            </Form>
        </div>
    )
}
