import React, { useState, useEffect, useRef } from 'react';
import { FormControl, Form, Button } from 'react-bootstrap';
import axios from 'axios';

// https://www.youtube.com/watch?v=EmAc4wQikwY
export default function About(){

    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("") 


    function onSubmit(e){
        e.preventDefault();
        var loginData = {
            "username": username,
            "password": password,
        }
        var token = "";
        const config = {
            headers: {
                "Content-Type": "application/json",
            }
        }

        axios.post("/account/api/auth/signin", loginData)
        .then(res=>{
            console.log(res)
            token = res.data.token
            config.headers["Authorization"] = `Token ${token}`
        })
        .catch(err=>{
            console.log(err)
            return(
                <alert className="test">{err}</alert>
            )
        })

    }
    return(
        <div className="container">
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" placeholder="Username" value={username}
                    onChange={e=>{setUsername(e.target.value)}}/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={password}
                    onChange={e=>{setPassword(e.target.value)}}/>
                </Form.Group>
                <Button variant="primary" type="submit" onClick={(e)=>{onSubmit(e)}}>
                    Log In
                </Button>
                <Button variant="light" type="submit" onClick={(e)=>{}}>
                    Register
                </Button>
            </Form>

        </div>
    )
}
