import React, { useState, useEffect, useRef } from 'react';
import { FormControl, Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';

// https://www.youtube.com/watch?v=EmAc4wQikwY
export default function About(){

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [token, setToken] = useState("");
    const [isError, setIsError] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);

    function renderError(){
        if (isError){
            return(
                <Alert className="mt-3" variant="danger" onClose={() => setIsError(false)} dismissible>
                    <Alert.Heading>Your username or password may be invalid</Alert.Heading>
                </Alert>
            )
        }
    }

    function renderSucess(){
        if (isError){
            return(
                <Alert transition={Fade} className="mt-3" variant="success" onClose={() => setIsSuccess(false)} dismissible>
                    <Alert.Heading>Your username or password may be invalid</Alert.Heading>
                </Alert>
            )
        }
    }

    function onSubmit(e){
        e.preventDefault();
        var loginData = {
            "username": username,
            "password": password,
        }
        const config = {
            headers: {
                "Content-Type": "application/json",
            }
        }

        axios.post("/account/api/auth/signin", loginData)
        .then(res=>{
            if(res){
                setIsError(false)
                setToken(res.data.token)
                config.headers["Authorization"] = `Token ${token}`
            }
        })
        .catch(err=>{
            console.log(err)
            setIsError(true)
        })

    }
    return(
        <div className="container">
            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" placeholder="Username" value={username}
                    onChange={e=>{setUsername(e.target.value)}}/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={password}
                    onChange={e=>{setPassword(e.target.value)}}/>
                </Form.Group>

                <Button className="btn btn-primary me-3" type="submit" onClick={(e)=>{onSubmit(e)}}>
                    Log In
                </Button>
                <Link to="/register" className="btn btn-dark">
                    Register
                </Link>
            </Form>
            {renderError()}

        </div>
    )
}
