import axios from 'axios';
import React, { useState, useEffect, useRef } from 'react'
import { Link } from 'react-router-dom';
import "./ListArticle.css"

export default function ListArticle(){
    const [article, setArticle] = useState({});
    const [thumbImg, setThumbImg] = useState({});


    function getArticle(){
        axios.get("/api/article").then(response => {
                setArticle(response.data);        
        })
    }





    function displayArticle(item){
        if (item.length > 0){
            let res = article.map(c => {
                var content = "";
                if(c.content.length > 180){
                    content = c.content.substring(0, 180)+"..." 
                }else{
                    content = c.content
                }
                console.log(thumbImg.current);
                return(
                    <Link to={"/article/" + c.id}>
                        <div class="tile"> 
                            {/* <img src={'https://source.unsplash.com/random?sig='+ c.id} alt="unsplah image"/> */}
                            <img src={thumbImg.current} alt="unsplah"/>
                            <div class="text">
                                <h1>{c.title}.</h1>
                                <h2 class="animate-text">{c.author}</h2>
                                <p class="animate-text">{content}</p>
                            </div>
                        </div> 
                    </Link>         
                )
            })
            return res
        }else{
            return <h2>You don't have any articles</h2>
        }
    }

    useEffect(() => {
        getArticle()
        function getUnsplash(){
            // becareful the rate limit which is 50/hour
            var config ={
                headers: {
                    "Accept-Version": "v1",
                    "Authorization": "Client-ID b_oOP9fLWgawnVSEWkoWlHMzoGD7J3uOk4DLp8u9E0w",
                }
            }
            axios.get("https://api.unsplash.com/photos/random", config)
            .then(res=>{

                thumbImg(res.data.urls.thumb);
                console.log(res)
            })
        }
        getUnsplash()


    }, [])
    return(
        <div class="wrap">
            {displayArticle(article)}
        </div>



    )
}
