import React, { useState, useEffect } from 'react'
import axios from 'axios';

export default function SingleArticle(props){
    const [article, setArticle] = useState({});

    function getArticle(props){
        axios.get("/api/article/"+props.ID).then(response => {
                setArticle(response.data);        
        })
    }

    useEffect(() => {
        getArticle(props);
    }, [props])

    if (!(Object.keys(article).length === 0 && article.constructor === Object)){
        return(
            <article class="post">
                <div>
                    <div class="absolute-bg" style="background-image: url('https://source.unsplash.com/f9C8ytxaItI/2000x1200');"></div>
                </div>
                <div class="post__container">
                    <span class="post__category">{article.author}</span>

                    <div class="post__content">
                    <header>
                        <time class="post__time">Jan 22 2017</time>
                        <h1 class="post__header"><h1>{article.title}</h1></h1>
                    </header>

                    <p class="post__text">{article.content}</p>
                    </div>
                    <div class="post__link">
                    </div>
                </div>
            </article>

            // <div className="container">
            //     <h1>{article.title}</h1>
            //     <p>{article.content}</p>
            // </div>
        )
    }else{
        return(
            <div className="container h-100 vh-100 d-flex align-items-center justify-content-center">
                <h2 className="">Sorry, the article you are looking for is not available</h2>
            </div>
        )
    }
}



