import React, { useState, useEffect, useRef } from 'react';
import { FormControl, Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';

// https://www.youtube.com/watch?v=EmAc4wQikwY
export default function Register(){

    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [token, setToken] = useState("");
    const [isError, setIsError] = useState(false);

    function renderError(){
        if (isError){
            return(
                <Alert className="mt-3" variant="danger" onClose={() => setIsError(false)} dismissible>
                    <Alert.Heading>Your username or password may be invalid</Alert.Heading>
                </Alert>
            )
        }
    }

    function onSubmit(e){
        e.preventDefault();
        var registerData = {
            "username": username,
            "email": email,
            "password": password,
        }
        const config = {
            headers: {
                "Content-Type": "application/json",
            }
        }

        axios.post("/account/api/auth/register", registerData)
        .then(res=>{
            if(res){
                setToken(res.data.token)
                config.headers["Authorization"] = `Token ${token}`
            }
        })
        .catch(err=>{
            console.log(err)
            setIsError(true)
        })

    }
    return(
        <div className="container">
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="username" placeholder="Username" value={username}
                    onChange={e=>{setUsername(e.target.value)}}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="Email" placeholder="Email" value={email}
                    onChange={e=>{setEmail(e.target.value)}}/>
                </Form.Group>

                <Form.Group className="mb-3">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={password}
                    onChange={e=>{setPassword(e.target.value)}}/>
                </Form.Group>

                <Button className="btn btn-primary me-3" type="submit" onClick={(e)=>{onSubmit(e)}}>
                    Log In
                </Button>
                <Link to="/register" className="btn btn-dark">
                    Register
                </Link>
            </Form>
            {renderError()}

        </div>
    )
}
